div.script:
var canvas, context, canvaso, contexto;
canvaso = document.getElementById('TangentaLogo');
context = canvaso.getContext('2d');
context.lineWidth = 1;

// Draw vertical line
context.strokeStyle ='#936b20';
context.beginPath();
context.moveTo(30, 0);
context.lineTo(30, 50);
context.stroke();

// Draw large arc
context.beginPath();
context.arc(54, 26, 24,0.6*Math.PI,1.5*Math.PI, false);
context.stroke();

// Draw head circle
context.beginPath();
context.fillStyle = "#E2FFB0"/*"#CEF988";*/
context.arc(34, 6, 4,0,2*Math.PI, false);
context.fill();
context.stroke();

context.closePath();
context.restore();
